/// <reference types="cypress" />
describe('Window', () => {
  before(() => {
    cy.visit(
      'https://glebbahmutov.com/cypress-examples/9.0.0/commands/window',
    )
  })

  it('window', () => {
    cy.window().should('have.property', 'top')
  })

  it('document', () => {
    cy.document()
      .should('have.property', 'charset')
      .and('eq', 'UTF-8')
  })

  it('title', () => {
    cy.title().should('include', 'Window APIs')
  })
})
